import React from 'react'
import Footer from '../components/Footer';
import { Formik , Form, Field, FieldArray, ErrorMessage } from 'formik';
import * as yup from 'yup';

const SignIn = () => {
    const [success, setSuccess] = useState(false);
    const [error, setError] = useState(false);
    const formInitialSchema = {
        email : '',
        password : ''
    }

    const formValidation = yup.object().shape({
        email : yup.string().required('Email is required').email('Please Enter Valid Email Address'),
        password : yup.string().required('Password is required')
    });

    // const handleFormSubmit = (values) => {
    //     console.log("submitted values", values)
    // }

  return (
    <section className='signup-page'>
        <div className='sign-left'>
            <div className='left-content'>
            <h1> Hello, Friend!</h1>
            <p>
Enter your personal details and start journey with us</p>
            </div>
        </div>
        <div className='sign-right'>
            <div></div>
            <div className='main-form'>
                <h2>Sign In Here</h2>
                <Formik initialValues={formInitialSchema} 
                validationSchema={formValidation}
                onSubmit={async (values) => {
                    const data = {
                                first_name: values.first_name,
                                last_name: values.last_name,
                                email: values.email,
                                contact_no: values.contact_no,
                                password: values.password,
                                confirm_password: values.confirm_password,
                                business_name: values.business_name,
                                business_description: values.business_description,
                        }
                
                        const axiosConfig = {
                            headers: {
                                'Accept': 'application/json',
                            },
                        }
                
                        const response = await axios.post(
                            'https://rsacarbook.jaraware.com/api/v1/register',
                            data,
                            axiosConfig)

                            .then((response) => {
                                console.log('response',data)
                                setSuccess('You have successfully Registered');
                                setError('');
                                navigate("/signin");
                            })
                            .catch((e) => {
                                console.log('Error',data)
                                setError('Email or Contact Number is aleready Exist');
                                setSuccess('');
                            });
                
                    }
                }
                >
                    <Form>
                        <div className='form-group'>
                            <label>Enter Your Email :</label>
                            <Field 
                            type="email"
                            name="email"
                            placeholder="Enter Your Email"
                            />
                            <p className='error-msg'>
                                <ErrorMessage name='email' />
                            </p>
                        </div>
                        <div className='form-group'>
                            <label>Enter Your Password :</label>
                            <Field 
                            type="password"
                            name="password"
                            placeholder="Enter Your Password"
                            />
                            <p className='error-msg'>
                                <ErrorMessage name='password' />
                            </p>
                        </div>
                        <div className='submit-btn'>
                        <button 
                        className='custom-btn' 
                        type='submit'>Sign In</button>
                        </div>
                    </Form>
                </Formik>
               
            </div>
            <Footer />
        </div>
    </section>
  )
}

export default SignIn;
