import React, { useEffect, useState } from 'react'
import Header from "../components/Header";
import Footer from '../components/Footer';
import { Formik, Form, Field, FieldArray, ErrorMessage } from 'formik';
import * as yup from 'yup';
import axios from 'axios';
import { Await, Link, useNavigate } from 'react-router-dom';


const UpdateProfile = () => {

    const [success, setSuccess] = useState(false);
    const [error, setError] = useState(false);
    const navigate = useNavigate();
    const [username, setUsername] = useState([]);
    const [lastname, setLastname] = useState([]);
    const [contactnumber, setContactnumber] = useState([]);
    const [bname, setBname] = useState([]);
    const [bdesc, setBdesc] = useState([]);
    const [cityid, setCityid] = useState([]);
    const [file, setFile] = useState('');

    useEffect(() => {

        const axiosConfig = {
            headers: {
                'Accept': 'application/json',
                Authorization: 'Bearer ' + localStorage.getItem("token"),
            }
        }
        axios
            .get('https://rsacarbook.jaraware.com/api/v1/getUserData', axiosConfig)
            .then((res) => {
                setUsername(res.data.data.user.first_name);
                setLastname(res.data.data.user.last_name);
                setContactnumber(res.data.data.user.contact_no);
                setBname(res.data.data.user.business.business_name);
                setBdesc(res.data.data.user.business.business_description);
                setCityid(res.data.data.user.city_id);
            })
            .catch((err) => {
                console.log(err);
            });
    }, []);
    const formInitialSchema = {
        business_name: '',
        business_description: '',
        first_name: '',
        contact_no: '',
        last_name: '',
        city_id: '',
    }
    const formValidationSchema = yup.object().shape({
        business_name: yup.string().required('Business Name is required'),
        business_description: yup.string().required('business_description required'),
        first_name: yup.string().required('first_name is required'),
        contact_no: yup.string().required('contact_no is required'),
        last_name: yup.string().required('last_name is required'),
        city_id: yup.string().required('city_id is required'),
    })

    const removeToken = (userToken) => { // export function from module 
        localStorage.removeItem("token");
        navigate("/");
        // setToken(null);
    }
    const proPic = localStorage.getItem("profilepic");
    const uname = localStorage.getItem("first_name");

    return (
        <>
            <header className='dash-header'>
                <div className='app-logo'>
                    <h1><Link to="/dashboard">Prakash</Link></h1>
                </div>
                <div className='app-right'>
                    <p>Hi <span className='white-color'>{uname}</span>
                        <img className='pro-pic' alt="pro" src={proPic} />
                        <button className="custom-btn" onClick={removeToken}>Log Out</button>
                    </p>
                </div>
            </header>
            <section className='signup-page dashboard'>
                <div className='d-flex'>
                    <div className='sign-left'>
                        <ul className='user-pages'>
                            <li><Link className='custom-btn' to='/updateprofile'>Update Your Profile</Link> </li>
                            <li><Link className='custom-btn' to='/uplaodDocument'>Upload Your Documents</Link> </li>
                            <li><Link className='custom-btn' to='/inquery'>Inquery</Link> </li>
                            <li><Link className='custom-btn' to='/pricing'>Pricing</Link> </li>
                            <li><button className="custom-btn" onClick={removeToken}>Log Out</button></li>
                        </ul>
                    </div>
                    <div className='sign-right'>
                        <div className='main-form'>
                            <h2>Update Your Profile Details :</h2>
                            <Formik
                                initialValues={formInitialSchema}
                                validationSchema={formValidationSchema}
                                onSubmit={
                                    async (values) => {
                                        
                                        const data = {
                                            business_name: values.business_name,
                                            business_description: values.business_description,
                                            first_name: values.first_name,
                                            last_name: values.last_name,
                                            contact_no: values.contact_no,
                                            city_id: values.city_id,
                                            profile_pic : values.profile_pic,
                                        }
                                        const axiosConfig = {
                                            headers: {
                                                'Accept': 'application/json',
                                                Authorization: 'Bearer ' + localStorage.getItem("token"),
                                            }
                                        }
                                        const formData = new FormData();
                                        formData.append('profile_pic', file);
                                        const response = await axios.post(
                                            'https://rsacarbook.jaraware.com/api/v1/updateUserData',
                                            data,
                                            axiosConfig
                                        ).then((response) => {
                                            console.log('response', response);
                                            setSuccess(response.data.meta.message);
                                        })
                                            .catch((err) => {
                                                console.log("error", response);
                                                setError(err.data.meta.message);
                                            })

                                    }
                                }
                            >
                                <Form>
                                    <div className='form-group'>
                                        <label>Enter Your first_name:</label>
                                        <Field
                                            type="text"
                                            name="first_name"
                                            placeholder={username}
                                        />
                                        <p className='error-msg'>
                                            <ErrorMessage name='first_name' />
                                        </p>
                                    </div>
                                    <div className='form-group'>
                                        <label>Enter Your last_name:</label>
                                        <Field
                                            type="text"
                                            name="last_name"
                                            placeholder={lastname}
                                        />
                                        <p className='error-msg'>
                                            <ErrorMessage name='last_name' />
                                        </p>
                                    </div>
                                    <div className='form-group'>
                                        <label>Enter Your contact_no:</label>
                                        <Field
                                            type="text"
                                            name="contact_no"
                                            placeholder={contactnumber}
                                        />
                                        <p className='error-msg'>
                                            <ErrorMessage name='contact_no' />
                                        </p>
                                    </div>
                                    <div className='form-group'>
                                        <label>Enter Your city_id:</label>
                                        <Field
                                            type="text"
                                            name="city_id"
                                            placeholder={cityid}
                                        />
                                        <p className='error-msg'>
                                            <ErrorMessage name='city_id' />
                                        </p>
                                    </div>
                                    <div className='form-group'>
                                        <label>Enter Your Business Name:</label>
                                        <Field
                                            type="text"
                                            name="business_name"
                                            placeholder={bname}
                                        />
                                        <p className='error-msg'>
                                            <ErrorMessage name='business_name' />
                                        </p>
                                    </div>
                                    <div className='form-group'>
                                        <label>Enter Your Business Description</label>
                                        <Field
                                            type="text"
                                            name="business_description"
                                            placeholder={bdesc}
                                        />
                                        <p className='error-msg'>
                                            <ErrorMessage name='business_description' />
                                        </p>
                                    </div>
                                    <div className='form-group'>
                                        <label>Enter Your profile_pic :</label>
                                        <Field
                                            type="file" name='profile_pic'
                                            onChange={(e) => setFile(e.target.files[0])}
                                            // placeholder={bdesc}
                                        />
                                        <p className='error-msg'>
                                            <ErrorMessage name='profile_pic' />
                                        </p>
                                    </div>
                                    
                                    
                                    <div className='submit-btn form-group'>
                                        <button
                                            className='custom-btn'
                                            type='submit'>Upadte Profile</button>
                                    </div>
                                    <div className='form-group back-success'>
                                        {success}
                                    </div>
                                    <div className='form-group back-error'>
                                        {error}
                                    </div>
                                </Form>
                            </Formik>
                        </div>
                    </div>
                </div>
                <Footer />

            </section>
        </>
    )
}

export default UpdateProfile;
